mapper.insert:
    /* kr.letech.demo.cmn.cmnt.dao.CmntDAO.insertCmnt */

      SELECT NVL(MAX(CMNT_SEQ), 0) + 1
        FROM TB_CMNT
       WHERE BOARD_NO = #{boardNo}

    INSERT INTO TB_CMNT (
        CMNT_SEQ
			,	BOARD_NO
			,	CMNT_CONT
			,	RGST_ID
			,	RGST_DT
			,	DEL_YN
    )
    VALUES (
        #{cmntSeq   , jdbcType=NUMERIC}
      , #{boardNo   , jdbcType=NUMERIC}
      , #{cmntCont  , jdbcType=VARCHAR}
      , #{rgstId    , jdbcType=VARCHAR}
      , NOW()
      , 'N'
    )

mapper.update:
/* kr.letech.demo.cmn.cmnt.dao.CmntDAO.updateCmnt */
UPDATE TB_CMNT
   SET CMNT_CONT = #{cmntCont, jdbcType=VARCHAR}
     , UPDT_DT = NOW()
 WHERE 1 = 1
   AND BOARD_NO = #{boardNo}
   AND CMNT_SEQ = #{cmntSeq}

/* kr.letech.demo.cmn.cmnt.dao.CmntDAO.deleteCmnt */
UPDATE TB_CMNT
   SET UPDT_DT = NOW()
     , DEL_YN = 'Y'
 WHERE 1 = 1
   AND BOARD_NO = #{boardNo}
   AND CMNT_SEQ = #{cmntSeq}

/* kr.letech.demo.cmn.cmnt.dao.CmntDAO.deleteCmntAll */
UPDATE TB_CMNT
   SET UPDT_DT = NOW()
     , DEL_YN = 'Y'
 WHERE 1 = 1
   AND BOARD_NO = #{boardNo}

mapper.select:
/* kr.letech.demo.cmn.cmnt.dao.CmntDAO.selectBoardOneCmntRgstId */
SELECT RGST_ID      AS rgstId
  FROM TB_CMNT
 WHERE 1 = 1
   AND BOARD_NO = #{boardNo}
   AND CMNT_SEQ = #{cmntSeq}
